import React from 'react'
import { connect } from 'react-redux';
// import { createStructuredSelector } from 'reselect';

import { clearItemFromCart, removeItem, addItem } from '../../redux/cart/cart.actions';    // add actions here

import {CheckoutItemContainer, ImgContainer, Img, NameContainer,QuantityContainer, ValueContainer, ArrowContainer, RemoveButtonContainer, PriceContainer} from './checkout-item.styles.jsx';

const CheckoutItem = ({ cartItem, clearItem, addItem, removeItem }) => {
  const {name, imageUrl, price, quantity} = cartItem;
  return (
    <CheckoutItemContainer>
        <ImgContainer>
            <Img src={imageUrl} alt="item"/>
        </ImgContainer>
        <NameContainer>{name}</NameContainer>
        <QuantityContainer>
            <ArrowContainer onClick={() => removeItem(cartItem)}>&#10094;</ArrowContainer>
            <ValueContainer>{quantity}</ValueContainer>
            <ArrowContainer onClick={() => addItem(cartItem)}>&#10095;</ArrowContainer>
        </QuantityContainer>
        <PriceContainer>{price}</PriceContainer>
        <RemoveButtonContainer onClick={() => clearItem(cartItem)}>&#10005;</RemoveButtonContainer>
    </CheckoutItemContainer>
  )
}

const mapDispatchToProps = dispatch => ({
    clearItem: item => dispatch(clearItemFromCart(item)),
    addItem: item => dispatch(addItem(item)),
    removeItem: item => dispatch(removeItem(item))
});

// const mapStateToProps = createStructuredSelector({
//     myProp: selectMyProp
// });

export default connect(null,mapDispatchToProps)(CheckoutItem);
